import React from 'react';

class Table extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      students: [],
      grades:[]
    };
  }

  componentDidMount() {
    let url1 = 'http://localhost:3000/api/students';
    let url2 = 'http://localhost:3000/api/grades';

    let apiRequest1 = fetch(url1).then(response => response.json());
    let apiRequest2 = fetch(url2).then(response => response.json());

    Promise.all([apiRequest1, apiRequest2]).then(resp => {
      this.setState({
        students: resp[0].data,
        grades: resp[1].data
      })
    });
  }

  calculateAvgGrade(scores) {
    let total = 0;
    let index = 0;
    Object.keys(scores).forEach(key => {
      if ((key !== 'student_id') && (key !== '_id')) {
        total = total + Number(scores[key]);
        index++;
      }
    });
    return Math.round(total / index);
  }

  calculateClassAverage(grades) {
    const total = 0;

    grades.forEach(score => {
      console.log(score);
    })
  }

  renderTableData() {
    return this.state.students.map((student, i) => {
      return (
        <div className="list-row" key={student._id}>
          <div className="cell student-info">
            <div className="name">
              <span className="name-block">{student.firstname}</span>
              <span>{student.lastname}</span>
            </div>
            <div className="average-grade">{this.calculateAvgGrade(student.scores[0])}%</div>
          </div>
          <div className="cell">{student.scores[0].chapter1Test}</div>
          <div className="cell">{student.scores[0].chapter2Test}</div>
          <div className="cell">{student.scores[0].chapter3Test}</div>
          <div className="cell">{student.scores[0].chapter4Test}</div>
          <div className="cell">{student.scores[0].chapter5Test}</div>
          <div className="cell">{student.scores[0].chapter6Test}</div>
          <div className="cell">{student.scores[0].chapter7Test}</div>
          <div className="cell">{student.scores[0].chapter8Test}</div>
          <div className="cell">{student.scores[0].chapter9Test}</div>
          <div className="cell">{student.scores[0].chapter10Test}</div>
        </div>
      );
    });
  }

  render () {
    return (
      <div>
        <h1>Gradebook Sample App</h1>
        <div className="resource-list">
          <div className="list-row list-header">
            <div className="column-spacer"></div>
            {this.calculateClassAverage(this.state.grades)}
            <div className="cell">Chapter 1 Test</div>
            <div className="cell">Chapter 2 Test</div>
            <div className="cell">Chapter 3 Test</div>
            <div className="cell">Chapter 4 Test</div>
            <div className="cell">Chapter 5 Test</div>
            <div className="cell">Chapter 6 Test</div>
            <div className="cell">Chapter 7 Test</div>
            <div className="cell">Chapter 8 Test</div>
            <div className="cell">Chapter 9 Test</div>
            <div className="cell">Chapter 10 Test</div>
          </div>
          {this.renderTableData()}
        </div>
      </div>
    )
  }

}

export default Table;
