import React from 'react';
import PropTypes from 'prop-types';
import Row from './row';

class Table extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      students: [
        {
          id: 101,
          firstname: 'Wade',
          lastname: 'Wilson',
        },
        {
          id: 102,
          firstname: 'Steve',
          lastname: 'Rogers',
        },
        {
          id: 103,
          firstname: 'Bucky',
          lastname: 'Barnes'
        }
      ]
    };
  }

  calculatePosition() {
    return this.props.offset + 'px';
  }

  renderBody() {
    const {
      rows,
      columns,
      startIndex
    } = this.props;

    let body;

    if (rows.length) {
      body = rows.map((row, i) => {
        return (
          <Row
            x={startIndex + i}
            key={this.props.getRowKey(row)}
            columns={columns}
            row={row}
            height={this.props.rowHeight}
            columnWidthValues={this.props.columnWidthValues}
          />
        );
      });
    } else {
      body = (
        <div className="placeholder">
          {this.props.placeholder}
        </div>
      );
    }
    return body;
  }

  render() {
    return(
      <div className="gradebook-grid" style={{transform: `translate3d(0, ${this.calculatePosition()}, 0)`}}>
        {this.renderBody()}
      </div>
    );
  }
}

const tablePropTypes = {
    columns: PropTypes.arrayOf(
        PropTypes.shape({
            id: PropTypes.string,
            title: PropTypes.oneOfType([
                PropTypes.string,
                PropTypes.func
            ]),
            value: PropTypes.func.isRequired
        })
    ).isRequired,
    rows: PropTypes.arrayOf(PropTypes.any),
    getRowKey: PropTypes.func.isRequired,
    placeholder: PropTypes.string,

    // scroll
    headerHeight: PropTypes.number,
    rowHeight: PropTypes.number,

    // resize
    columnWidthValues: PropTypes.object
};


Table.propTypes = Object.assign({}, tablePropTypes, {
  offset: PropTypes.number.isRequired,
  startIndex: PropTypes.number.isRequired,
  rowsCount: PropTypes.number.isRequired,
});

export default Table;
