import React from 'react';

class Table extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      grades:[]
    };
  }

  componentDidMount() {
    let url = 'http://localhost:3000/api/grades';
    fetch(url)
      .then(response => response.json())
      .then(resp => {
        this.setState({
          grades: resp.data
        })
    });
  }

  formatGradesData() {
    let obj = this.state.grades[0];
    let names = Object.keys(obj);
    console.log(names);
  }

  renderTableData() {
    return this.state.grades.map((student, i) => {
      return (
        <div className="list-row" key={student._id}>
          <div className="cell student-info">
            <div className="name">
              <span className="name-block">{student.student[0].firstname}</span>
              <span>{student.student[0].lastname}</span>
            </div>
            <div className="average-grade">85%</div>
          </div>
          <div className="cell">{student.chapter1Test}</div>
          <div className="cell">{student.chapter2Test}</div>
          <div className="cell">{student.chapter3Test}</div>
          <div className="cell">{student.chapter4Test}</div>
          <div className="cell">{student.chapter5Test}</div>
          <div className="cell">{student.chapter6Test}</div>
          <div className="cell">{student.chapter7Test}</div>
          <div className="cell">{student.chapter8Test}</div>
          <div className="cell">{student.chapter9Test}</div>
          <div className="cell">{student.chapter10Test}</div>
        </div>
      );
    });
  }

  render () {
    return (
      <div>
        <h1>Gradebook Sample App</h1>
        <div className="resource-list">
          <div className="list-row list-header">
            <div className="column-spacer"></div>
          </div>
          {this.renderTableData()}
        </div>
      </div>
    )
  }

}

export default Table;
