import React from 'react';

class Table extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      students: [
        {
          id: 101,
          firstname: 'Wade',
          lastname: 'Wilson',
          score: '90%'
        },
        {
          id: 102,
          firstname: 'Steve',
          lastname: 'Rogers',
          score: '90%'
        },
        {
          id: 103,
          firstname: 'Bucky',
          lastname: 'Barnes',
          score: '90%'
        }
      ]
    };
  }

  renderTableData() {
    return this.state.students.map((student, i) => {
      const { id, firstname, lastname, score } = student;

      return (
        <div className="list-row" key={id}>
          <div className="cell">
            <div className="student-info">
              <span className="name">{firstname}</span>
              <span>{lastname}</span>
            </div>
            <div>85%</div>
          </div>
          <div className="cell">{score}</div>
        </div>
      );
    });
  }

  render () {
    return (
      <div>
        <h1>Gradebook Sample App</h1>
        <div className="resource-list">
          <div className="list-row list-header">
            <div className="column-heading student-info"></div>
            <div className="column-heading">Chapter 1 Test</div>
            <div className="column-heading">Chapter 2 Test</div>
            <div className="column-heading">Chapter 3 Test</div>
            <div className="column-heading">Chapter 4 Test</div>
            <div className="column-heading">Chapter 5 Test</div>
            <div className="column-heading">Chapter 6 Test</div>
          </div>
            {this.renderTableData()}
        </div>
      </div>
    )
  }

}

export default Table;
