import React from 'react';

import apis from '../api';

class Table extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      students: [],
      grades:[]
    };
  }

  componentDidMount() {
    let url1 = 'http://localhost:3000/api/students';
    let url2 = 'http://localhost:3000/api/grades';

    Promise.all([
      fetch(url1).then(response => response.json()),
      fetch(url2).then(response => response.json())
    ]).then(([students, grades]) => {
      this.setState({
        students: students,
        grades: grades
      });
    });
  }

  renderTableData() {
    return this.state.students.map((student, i) => {
      const { id, firstname, lastname } = student;

      return (
        <div className="list-row" key={id}>
          <div className="cell student-info">
            <div className="name">
              <span className="name-block">{firstname}</span>
              <span>{lastname}</span>
            </div>
            <div className="average-grade">85%</div>
          </div>
          <div className="cell">90%</div>
        </div>
      );
    });
  }

  renderColumnHeaders() {
    return this.state.grades.map((grade, i) => {
      return (
        <div className="column-heading">{grade}</div>
      )
    });
  }

  render () {
    return (
      <div>
        <h1>Gradebook Sample App</h1>
        <div className="resource-list">
          <div className="list-row list-header">
            <div className="column-spacer"></div>

          </div>
            {this.renderTableData()}
        </div>
      </div>
    )
  }

}

export default Table;
